import React, { useState } from 'react';

const AddTiffinCenter = () => {
  const [formData, setFormData] = useState({
    name: '',
    ownerName: '',
    contactNumber: '',
    email: '',
    address: '',
    subscriptionType: '',
    description: '',
    deliveryAreas: [] // New field for selecting areas
  });

  const [areas, setAreas] = useState({
    Area1: false,
    Area2: false,
    Area3: false,
    Area4: false
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleAreaChange = (e) => {
    const { name, checked } = e.target;
    setAreas({ ...areas, [name]: checked });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const selectedAreas = Object.keys(areas).filter(area => areas[area]);
    const dataToSubmit = { ...formData, deliveryAreas: selectedAreas };

    console.log('Tiffin Center Data:', dataToSubmit);
    // Call your register function here to save to Firebase or backend
    // registerTiffinCenter(dataToSubmit, selectedAreas);
  };

  return (
    <div className="tiffin-center-form">
      <h2>Register Your Tiffin Center</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Owner Name:</label>
          <input
            type="text"
            name="ownerName"
            value={formData.ownerName}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Contact Number:</label>
          <input
            type="text"
            name="contactNumber"
            value={formData.contactNumber}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Address:</label>
          <textarea
            name="address"
            value={formData.address}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Subscription Type:</label>
          <select
            name="subscriptionType"
            value={formData.subscriptionType}
            onChange={handleChange}
            required
          >
            <option value="">Select</option>
            <option value="daily">Daily</option>
            <option value="weekly">Weekly</option>
            <option value="monthly">Monthly</option>
          </select>
        </div>
        <div>
          <label>Description:</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            required
          />
        </div>

        {/* New Delivery Areas Section */}
        <div>
          <label>Delivery Areas:</label>
          <div>
            <label>
              <input
                type="checkbox"
                name="Area1"
                checked={areas.Area1}
                onChange={handleAreaChange}
              />
              Area 1
            </label>
          </div>
          <div>
            <label>
              <input
                type="checkbox"
                name="Area2"
                checked={areas.Area2}
                onChange={handleAreaChange}
              />
              Area 2
            </label>
          </div>
          <div>
            <label>
              <input
                type="checkbox"
                name="Area3"
                checked={areas.Area3}
                onChange={handleAreaChange}
              />
              Area 3
            </label>
          </div>
          <div>
            <label>
              <input
                type="checkbox"
                name="Area4"
                checked={areas.Area4}
                onChange={handleAreaChange}
              />
              Area 4
            </label>
          </div>
        </div>

        <div>
          <button type="submit">Register Tiffin Center</button>
        </div>
      </form>
    </div>
  );
};

export default TiffinCenterRegistrationForm;
