import React, { useState } from 'react';

const AddTiffinCenter = () => {
  const [formData, setFormData] = useState({
    name: '',
    ownerName: '',
    contactNumber: '',
    email: '',
    address: '',
    subscriptionType: '',
    description: '',
    deliveryAreas: [] // New field for selecting areas
  });

  const [areas, setAreas] = useState({
    Area1: false,
    Area2: false,
    Area3: false,
    Area4: false
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleAreaChange = (e) => {
    const { name, checked } = e.target;
    setAreas({ ...areas, [name]: checked });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const selectedAreas = Object.keys(areas).filter(area => areas[area]);
    const dataToSubmit = { ...formData, deliveryAreas: selectedAreas };

    console.log('Tiffin Center Data:', dataToSubmit);
    // Call your register function here to save to Firebase or backend
    // registerTiffinCenter(dataToSubmit, selectedAreas);
  };

  return (
    <div class="add-user-modal">
  <div class="modal-header">
    <h2>Add User</h2>
    <button class="close-btn">&times;</button>
  </div>
  
  <div class="modal-body">
    <div class="image-upload-section">
      <label for="image-upload" class="image-upload-label">
        <div class="upload-image-box">
          <span>UPLOAD IMAGE</span>
          <input type="file" id="image-upload" class="upload-image-input" accept="image/*" />
          <i class="fa fa-camera"></i>
        </div>
      </label>
    </div>
    
    <form class="user-form">
      <div class="form-group">
        <input type="text" placeholder="Full name*" required />
      </div>

      <div class="form-row">
        <div class="form-group">
          <input type="text" placeholder="Mobile number*" required />
        </div>
        <div class="form-group">
          <input type="email" placeholder="Email ID" />
        </div>
      </div>
      
      <div class="form-group">
        <select>
          <option value="">Role</option>
         
        </select>
      </div>
      
      <h4>Login Credential <span>(Below Credential will be used to login into account)</span></h4>

      <div class="form-row">
        <div class="form-group">
          <input type="text" placeholder="Username*" required />
        </div>
        <div class="form-group">
          <input type="password" placeholder="Password*" required />
        </div>
      </div>
    </form>
  </div>
  
  <div class="modal-footer">
    <button class="save-btn">Save</button>
    <button class="cancel-btn">Cancel</button>
  </div>
</div>

  );

  
  
  // Styles
  
  
  
  // Styles

  
};

export default AddTiffinCenter;

const formContainerStyle = {
  maxWidth: '800px',  // Increased width
  margin: '0 auto',
  padding: '20px',
  backgroundColor: '#fff',
  borderRadius: '15px',
  boxShadow: '0 10px 20px rgba(0, 0, 0, 0.1)',
  backgroundImage: 'linear-gradient(to bottom, #f9f9f9, #e3f2fd)',
};

const formTitleStyle = {
  textAlign: 'center',
  color: '#1976d2',
  fontFamily: 'Roboto, sans-serif',
  marginBottom: '20px',
  fontSize: '22px',
  fontWeight: '500',
};

const formStyle = {
  display: 'flex',
  flexDirection: 'column',
  gap: '20px', // Add gap for rows
};

const inputRowStyle = {
  display: 'flex',
  justifyContent: 'space-between', // Inputs side by side
  gap: '20px',  // Adjust space between columns
};

const inputGroupStyle = {
  flex: 1,  // Ensure inputs take equal space
  marginBottom: '15px',
};

const labelStyle = {
  fontSize: '14px',
  fontWeight: '600',
  color: '#333',
  marginBottom: '6px',
  display: 'block',
};

const inputStyle = {
  width: '60%',
  padding: '10px',
  fontSize: '14px',
  borderRadius: '6px',
  border: '1px solid #ccc',
  marginTop: '5px',
  transition: 'border-color 0.3s ease',
};

const textAreaStyle = {
  ...inputStyle,
  minHeight: '80px',
};

const selectStyle = {
  ...inputStyle,
};

const checkboxGroupStyle = {
  display: 'flex',
  flexDirection: 'column',
  gap: '8px',
};

const checkboxStyle = {
  marginRight: '10px',
  transform: 'scale(1.1)',
};

const buttonContainerStyle = {
  textAlign: 'center',
  marginTop: '25px',
};

const submitButtonStyle = {
  backgroundColor: '#1976d2',
  backgroundImage: 'linear-gradient(to right, #42a5f5, #1976d2)',
  color: '#fff',
  padding: '10px 20px',
  borderRadius: '6px',
  border: 'none',
  cursor: 'pointer',
  fontSize: '14px',
  transition: 'background-color 0.3s ease, box-shadow 0.3s ease',
};
