import React, { useState } from 'react';
import './addTiffinCenter.css'

const LoginComponent = () => {
  const [formData, setFormData] = useState({
    name: '',
    ownerName: '',
    contactNumber: '',
    email: '',
    address: '',
    subscriptionType: '',
    description: '',
    deliveryAreas: [] // New field for selecting areas
  });

  const [areas, setAreas] = useState({
    Area1: false,
    Area2: false,
    Area3: false,
    Area4: false
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleAreaChange = (e) => {
    const { name, checked } = e.target;
    setAreas({ ...areas, [name]: checked });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const selectedAreas = Object.keys(areas).filter(area => areas[area]);
    const dataToSubmit = { ...formData, deliveryAreas: selectedAreas };

    console.log('Tiffin Center Data:', dataToSubmit);
    // Call your register function here to save to Firebase or backend
    // registerTiffinCenter(dataToSubmit, selectedAreas);
  };

  return (
    <div style={styles.container}>
    <form onSubmit={handleSubmit} style={styles.form}>
      <h2>Login</h2>
      <div style={styles.inputContainer}>
        <label htmlFor="userName">User Name:</label>
        <input
          type="text"
          id="userName"
          name="userName"
          value={formData.userName}
          onChange={handleChange}
          style={styles.input}
          required
        />
      </div>
      <div style={styles.inputContainer}>
        <label htmlFor="password">Password:</label>
        <input
          type="password"
          id="password"
          name="password"
          value={formData.password}
          onChange={handleChange}
          style={styles.input}
          required
        />
      </div>
      <button type="submit" style={styles.button}>
        Login
      </button>
      <div style={styles.signupOption}>
        <p>Don't have an account?</p>
        <a href="/signup" style={styles.signupLink}>
          Sign Up
        </a>
      </div>
    </form>
  </div>
  

  );


};
const styles = {
  container: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh',
    backgroundColor: '#f0f0f0',
  },
  form: {
    backgroundColor: '#fff',
    padding: '20px',
    borderRadius: '5px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
  },
  inputContainer: {
    marginBottom: '15px',
  },
  input: {
    width: '100%',
    padding: '8px',
    marginTop: '5px',
    borderRadius: '4px',
    border: '1px solid #ccc',
  },
  button: {
    width: '100%',
    padding: '10px',
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  },
  signupOption: {
    marginTop: '15px',
    textAlign: 'center',
  },
  signupLink: {
    color: '#007bff',
    textDecoration: 'none',
  },
};

export default AddTiffinCenter;
