import React, { useState } from 'react';

const AddTiffinCenter = () => {
  const [formData, setFormData] = useState({
    name: '',
    ownerName: '',
    contactNumber: '',
    email: '',
    address: '',
    subscriptionType: '',
    description: '',
    deliveryAreas: [] // New field for selecting areas
  });

  const [areas, setAreas] = useState({
    Area1: false,
    Area2: false,
    Area3: false,
    Area4: false
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleAreaChange = (e) => {
    const { name, checked } = e.target;
    setAreas({ ...areas, [name]: checked });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const selectedAreas = Object.keys(areas).filter(area => areas[area]);
    const dataToSubmit = { ...formData, deliveryAreas: selectedAreas };

    console.log('Tiffin Center Data:', dataToSubmit);
    // Call your register function here to save to Firebase or backend
    // registerTiffinCenter(dataToSubmit, selectedAreas);
  };


  
  // Styles

  
};

export default AddTiffinCenter;



const labelStyle = {
  fontSize: '14px',
  fontWeight: 'bold',
  color: '#555',
};

const inputStyle = {
  width: '100%',
  padding: '10px',
  fontSize: '14px',
  borderRadius: '5px',
  border: '1px solid #ccc',
  marginTop: '5px',
};

const textAreaStyle = {
  width: '100%',
  padding: '10px',
  fontSize: '14px',
  borderRadius: '5px',
  border: '1px solid #ccc',
  marginTop: '5px',
  minHeight: '80px',
};

const selectStyle = {
  width: '100%',
  padding: '10px',
  fontSize: '14px',
  borderRadius: '5px',
  border: '1px solid #ccc',
  marginTop: '5px',
};

const checkboxGroupStyle = {
  display: 'flex',
  flexDirection: 'column',
};

const checkboxStyle = {
  marginRight: '10px',
};

const buttonContainerStyle = {
  textAlign: 'center',
  marginTop: '20px',
};

const submitButtonStyle = {
  backgroundColor: '#28a745',
  color: '#fff',
  padding: '10px 20px',
  borderRadius: '5px',
  border: 'none',
  cursor: 'pointer',
  fontSize: '16px',
  transition: 'background-color 0.3s ease',
};

submitButtonStyle[':hover'] = {
  backgroundColor: '#218838',
};
