import { createSlice } from '@reduxjs/toolkit';

const formSlice = createSlice({
  name: 'form',
  initialState: {
    formData: [{}],
  },
  reducers: {
    addFormData: (state, action) => {
      state.formData.push(action.payload); 
    },
    removeFormData: (state, action) => {
      state.formData = state.formData.filter((_, index) => index !== action.payload);
    },
    updateFormData: (state, action) => {
      const { index, newData } = action.payload;
      if (index >= 0 && index < state.formData.length) {
        state.formData[index] = newData;
      }
    }
  },
});

export const { addFormData,removeFormData ,updateFormData } = formSlice.actions;
export default formSlice.reducer;
