import { initializeApp } from "firebase/app";
// import { getAnalytics } from "firebase/analytics";
import { getDatabase } from "firebase/database";
import { getAuth,RecaptchaVerifier,signInWithPhoneNumber,PhoneAuthProvider,signInWithCredential } from "firebase/auth";

const firebaseConfig = {
  apiKey: "AIzaSyAisKRlcPjMTNDkNHYPpFodAjkFAIj64Jc",
  authDomain: "tiffinwala-9d7b8.firebaseapp.com",
  projectId: "tiffinwala-9d7b8",
  storageBucket: "tiffinwala-9d7b8.appspot.com",
  messagingSenderId: "677466160862",
  appId: "1:677466160862:web:97b7722bfc597b69efcc4f",
  measurementId: "G-C3BRQ6L0J3"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
export const database = getDatabase(app);
export const auth = getAuth(app); 
export const phone= new PhoneAuthProvider(app)
export const SignUp = signInWithCredential(app)
export const number = signInWithPhoneNumber(app)
export const setupRecaptcha = (elementId) => {
  const recaptchaVerifier = new RecaptchaVerifier(elementId, {
    size: "invisible", // or "normal" if you want a visible widget
  }, auth);
  return recaptchaVerifier;
};
