// src/components/DataList.js
import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { removeFormData } from '../../Redux/formSlice';

const DataList = () => {
  const [localFormData, setLocalFormData] = useState([ {name: 'anil sharma', email: 'anilsharma.wevois@gmail.com', age: '77', add: 'jaipur'}]);
  const formData = useSelector((state) => state.form.formData); // Access form data from Redux store

  const dispatch = useDispatch();

  useEffect(() => {
    setLocalFormData(formData);
  }, [formData])

  console.log(localFormData)
 
  const handleDelete = (index) => {
     dispatch(removeFormData(index));
  };
  return (
    <div>
      <h2>Submitted Data</h2>
      {localFormData.length === 0 ? (
        <p>No data submitted yet.</p>
      ) : (
        <ul>
          {localFormData.map((data, index) => (
            <li key={index}>
              <strong>Name:</strong> {data.name} | <strong>Email:</strong> {data.email} | <strong>Age:</strong> {data.age}|<strong>Address:</strong> {data.add}
              <button onClick={() => handleDelete(index)}>Delete</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default DataList;
